    public class LevelFactory
    {
        public static bool Exists(int level)
        {
            if (level >= 1 && level <= 3)
                return true;
            return false;
        }

        public static Level getLevel(int level, ContentManager content, Hero hero)
        {
            if (level == 1)
                return getLevel1(content, hero);
            if (level == 2)
                return getLevel2(content, hero);
            if (level == 3)
                return getLevel3(content, hero);
            return null;
        }

